const lecWriteContainer = document.getElementById("lecWrite");
const contentWriteContainer = document.getElementById("contentWrite");

for (let i = 0; i < lettrureWrites.length; i++) {
  lecWriteContainer.innerHTML += `
            <div class="card">
				<i class="fas fa-pen"></i>
				<div class="pra">
					<p>${lettrureWrites[i].slice(0, 300)} ...</p>
	
					<p style="text-align: center;">
                    <button onclick="handleClick(${i} , 'lec')" class="button">Read More</button>
					</p>
				</div>
            </div>
    `;
}

for (let i = 0; i < contentWrites.length; i++) {
  contentWriteContainer.innerHTML += `
            <div class="card">
				<i class="fas fa-pen"></i>
				<div class="pra">
					<p>${contentWrites[i].slice(0, 300)} ...</p>
	
					<p style="text-align: center;">
                    <button onclick="handleClick(${i} , 'con')" class="button">Read More</button>
					</p>
				</div>
            </div>
    `;
}

function handleClick(i, type) {
  if (modal.classList.contains("d-none")) {
    modal.classList.remove("d-none");
  } else {
    modal.classList.add("d-none");
  }

  const content = type === "lec" ? lettrureWrites[i] : contentWrites[i];
  modal.innerHTML = `
        <button onclick="handleClick(${i})" class="button">back</button>
        <div class="modal-card">
			<div class="pra">
				<p>${content}</p>
			</div>
		</div>
  `;
}
